import React from "react";
import { Typography, Box, Button, Avatar, Input } from "@material-ui/core";

import { makeStyles } from "@material-ui/core/styles";

import TextField from "@material-ui/core/TextField";
import * as Yup from "yup";
import { Formik } from "formik";

const logInValidationSchema = Yup.object().shape({
  email: Yup.string()

    .min(2, "Too Short!")
    .max(25, "Too Long!")
    .required("Email is required"),
  password: Yup.string()
    .min(2, "Too Short!")
    .max(25, "Too Long!")
    .min(6, "Password has to be longer than 6 characters!")
    .required("Valid password  is required"),
});

const useStyles = makeStyles({
  wrapper: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
  },
  card: {
    borderRadius: "5px",
    width: "350px",
    height: "340px",
    backgroundColor: "#F7F7F7",
    padding: "20px",
    margin: "80px",
  },
  inputStyle: {
    backgroundColor: "#ffffff",
    border: "1px solid #CCCCCC",
    // borderBottom: "1px solid #CCCCCC",
    borderBottom: "none",
    boxShadow: "none",
    borderRadius: "4px",
    padding: "0px 10px",
  },
});

const initialValues = {
  email: "",
  password: "",
};

const Index = () => {
  const classes = useStyles();
  return (
    <div className={classes.wrapper}>
      <Box className={classes.card}>
        <Box
          style={{
            paddingTop: "25px",
            marginBottom: "10 px",
            textAlign: "center",
          }}
        >
          <Typography style={{ fontSize: "35px", textAlign: "center" }}>
            Log in
          </Typography>
        </Box>

        <Formik
          initialValues={initialValues}
          onSubmit={(values, { resetForm }) => {
            console.log(values);
          }}
          validationSchema={logInValidationSchema}
        >
          {({
            values,
            handleChange,
            handleBlur,
            handleSubmit,
            errors,
            touched,
          }) => {
            console.log(errors);
            return (
              <Box>
                <Box style={{ marginLeft: "65px  ", marginRight: "65px" }}>
                  <Typography>Email:</Typography>
                  <Box className={classes.inputStyle}>
                    <Input
                      style={{
                        border: "1px solid #CCCCCC",
                        borderBottom: "1px solid #CCCCCC",
                      }}
                      name="email"
                      value={values.email}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      placeholder="Enter your Email"
                    />
                  </Box>
                  <Typography style={{ fontSize: 12, color: "red" }}>
                    {touched.email && errors.email}
                  </Typography>
                </Box>
                <Box
                  style={{
                    marginLeft: "65px ",
                    marginRight: "65px",
                    marginTop: "15px",
                  }}
                >
                  <Typography type="password">Password:</Typography>
                  <Box className={classes.inputStyle}>
                    <Input
                      name="password"
                      type="password"
                      value={values.password}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      placeholder="Enter your Password "
                    />
                  </Box>
                  <Typography style={{ fontSize: 12, color: "red" }}>
                    {touched.password && errors.password}
                  </Typography>
                </Box>
                <Box style={{ marginLeft: "65px" }}>
                  <Button
                    style={{
                      marginTop: "50px",
                      backgroundColor: "#347AB7",
                      color: "white",
                      width: "80%",
                    }}
                    variant="contained"
                  >
                    Log in
                  </Button>
                </Box>
              </Box>
            );
          }}
        </Formik>
      </Box>
    </div>
  );
};
export default Index;
