                  <Input
                    name="fname"
                    type="text"
                    value={values.fname}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    disableUnderline={true}
                    placeholder="Enter your first name "
                  />
                </Box>
              </Box>

              <Box className={inputWrapper}>
                <Typography> Last Name</Typography>
                <Box className={inputStyle}>
                  <Input
                    name="lname"
                    type="text"
                    value={values.lname}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    disableUnderline={true}
                    placeholder="Enter your Last  name "
                  />
                </Box>
              </Box>

              <Box className={inputWrapper}>
                <Typography> Email</Typography>
                <Box className={inputStyle}>
                  <Input
                    style={{ backgroundColor: "#ffffff" }}
                    name="email"
                    type="email"
                    value={values.email}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    disableUnderline={true}
                    placeholder="Enter your Email "
                  />
                </Box>
              </Box>

              <Box className={inputWrapper}>
                <Typography> Password</Typography>
                <Box className={inputStyle}>
                  <Input
                    style={{ backgroundColor: "#ffffff" }}
                    name="password"
                    type="password"
                    value={values.password}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    disableUnderline={true}
                    placeholder=" "
                  />
                </Box>
              </Box>

              <Box className={inputWrapper}>
                <Typography>Confirm Password </Typography>
                <Box className={inputStyle}>
                  <Input
                    style={{ backgroundColor: "#ffffff" }}
                    name="cpassword"
                    type="password"
                    value={values.cpassword}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    disableUnderline={true}
                    placeholder=" "
                  />
                </Box>
              </Box>

              <style jsx global>{`
                body {
                  margin: 0px;
                }
              `}</style>

              <Box
                style={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-between",
                }}
              >
                <Button
                  style={{
                    marginTop: "50px",
                    backgroundColor: "#347AB7",
                    color: "white",
                    width: "9 %",
                  }}
                  variant="contained"
                >
                  Register
                </Button>
                <Button
                  style={{
                    marginTop: "50px",
                    backgroundColor: "#347AB7",
                    color: "white",
                    width: "8%",
                  }}
                  variant="contained"
                >
                  Reset
                </Button>
              </Box>
            </Box>
          );
        }}
      </Formik>
    </Box>
  );
};
export default index;
