import React from "react";

import { Typography, Box, Input, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

import * as Yup from "yup";
import { Formik } from "formik";

const styles = makeStyles({
  wrapper: {
    backgroundColor: "#E9ECEF",
    height: "100vh",
    paddingTop: "3rem",

    display: "flex",
    flexDirection: "column",
    justifyContent: "flex-start",
    alignItems: "center",
  },
  inputWrapper: {
    borderRadius: "1px solid  #000000",
    marginBottom: "1rem",
  },
  inputStyle: {
    width: "300px",
    border: "1px solid #cccccc",
    backgroundColor: "#ffffff",
  },
});

const index = () => {
  const { wrapper, inputWrapper, inputStyle } = styles();
  return (
    <Box className={wrapper}>
      <Typography style={{ fontSize: "25px", marginBottom: "35px" }}>
        React Form Validation{" "}
      </Typography>
      <Box className={inputWrapper}>
        <Typography style={{}}> First Name</Typography>
        <Box className={inputStyle}>
          <Input
            type="text"
            disableUnderline={true}
            placeholder="Enter your first name "
          />
        </Box>
      </Box>

      <Box className={inputWrapper}>
        <Typography> Last Name</Typography>
        <Box className={inputStyle}>
          <Input
            type="text"
            disableUnderline={true}
            placeholder="Enter your Last  name "
          />
        </Box>
      </Box>

      <Box className={inputWrapper}>
        <Typography> Email</Typography>
        <Box className={inputStyle}>
          <Input
            style={{ backgroundColor: "#ffffff" }}
            type="email"
            disableUnderline={true}
            placeholder="Enter your Email "
          />
        </Box>
      </Box>

      <Box className={inputWrapper}>
        <Typography> Password</Typography>
        <Box className={inputStyle}>
          <Input
            style={{ backgroundColor: "#ffffff" }}
            type="password"
            disableUnderline={true}
            placeholder=" "
          />
        </Box>
      </Box>

      <Box className={inputWrapper}>
        <Typography>Confirm Password </Typography>
        <Box className={inputStyle}>
          <Input
            style={{ backgroundColor: "#ffffff" }}
            type="password"
            disableUnderline={true}
            placeholder=" "
          />
        </Box>
      </Box>

      <style jsx global>{`
        body {
          margin: 0px;
        }
      `}</style>
      <Button
        style={{

          marginTop: "50px",
          backgroundColor: "#347AB7",
          color: "white",
          width: "7%",
        }}
        variant="contained"
      >
        Register
      </Button>
    </Box>
  );
};
export default index;
